if (!item.binary || !item.binary.data || !item.binary.data.data) {
  throw new Error('Binary data is missing.');
}
const binaryData = item.binary.data.data;
// Set default mime type to video/mp4, but prefer the actual mimeType if present
const mimeType = item.binary.data.mimeType || 'video/mp4'; 

// Decode and re-encode base64
// Assuming binaryData is already a Buffer or a similar byte array.
// If it's a base64 string, you might not need the first Buffer.from() call.
// However, if the platform provides it as a byte array, this is correct.
const videoBase64 = Buffer.from(binaryData).toString('base64');

// Attach to item.json
item.json.videoBase64 = videoBase64;
item.json.mimeType = mimeType;

return item;
